        key: "type",
        minWidth: 120,
        tooltip: true,
        align: "center",
        sortable: false
      }, {
        title: "课题难度",
        key: "level",
        minWidth: 120,
        tooltip: true,
        align: "center",
        sortable: false
      }, {
        title: "剩余名额",
        key: "quota",
        minWidth: 120,
        tooltip: true,
        align: "center",
        sortable: false
      }, {
        title: "选题要求",
        key: "remark",
        minWidth: 150,
        tooltip: true,
        align: "center",
        sortable: false
      }, {
        title: "发布教师",
        key: "teaName",
        minWidth: 120,
        tooltip: true,
        align: "center",
        sortable: false
      }, {
        title: "是否被选择",
        key: "checkFlag",
        minWidth: 120,
        tooltip: true,
        align: "center",
        sortable: false,
        render: (h, params) => {
          if (params.row.checkFlag) {
            return h("div", [h("span", {
              style: {
                color: "#3CB371"
              }
            }, "已选择")]);
          } else {
            return h("div", [h("span", {
              style: {
                color: "#ff0000"
              }
            }, "未选择")]);
          }
        }
      }, {
        title: "选题人",
        key: "checkName",
        minWidth: 120,
        tooltip: true,
        align: "center",
        sortable: false
      }, {
        title: "是否被审核",
        key: "auditFlag",
        minWidth: 120,
        tooltip: true,
        align: "center",
        sortable: false,
        render: (h, params) => {
          if (params.row.auditFlag) {
            return h("div", [h("span", {
              style: {
                color: "#3CB371"
              }
            }, "已审核")]);
          } else {
            return h("div", [h("span", {
              style: {
                color: "#ff0000"
              }
            }, "未审核")]);
          }
        }
      }, {
        title: "创建时间",
        key: "createTime",
        sortable: true,
        sortType: "desc",
        minWidth: 180,
        align: "center",
        tooltip: true
      }, {
        title: "创建者",
        key: "createBy",
        sortable: true,
        sortType: "desc",
        minWidth: 100,
        align: "center",
        tooltip: true
      }, {
        title: "修改时间",
        key: "updateTime",
        minWidth: 180,
        align: "center",
        tooltip: true
      }, {
        title: "修改者",
        key: "updateBy",
        minWidth: 100,
        align: "center",
        tooltip: true
      }, {
        title: "操作",
        key: "action",
        align: "center",
        fixed: "right",
        width: 480,
        render: (h, params) => {
          return h("div", [h("Button", {
            props: {
              type: "primary",
              size: "small",
              icon: "ios-create-outline",
              ghost: true
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: () => {
                this.uploadFileFx(params.row);
              }
            }
          }, "上传"), h("Button", {
            props: {
              type: "primary",
              size: "small",
              icon: "ios-create-outline",
              ghost: true,
              disabled: params.row.taskFile === undefined
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: () => {
                this.downFileFx(params.row);
              }
            }
          }, "下载"), h("Button", {
            props: {
              type: "primary",
              size: "small",
              icon: "ios-create-outline",
              ghost: true
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: () => {
                this.edit(params.row);
              }
            }
          }, "编辑"), h("Button", {
            props: {
              type: "primary",
              size: "small",
              icon: "ios-create-outline",
              ghost: true,
              disabled: !(params.row.checkFlag && !params.row.auditFlag)
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: () => {
                this.auditFx(params.row);
              }
            }
          }, "审核"), h("Button", {
            props: {
              type: "primary",
              size: "small",
              icon: "ios-create-outline",
              ghost: true,
              disabled: !(params.row.checkFlag && !params.row.auditFlag)
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: () => {
                this.checkNotTopicFx(params.row);
              }
            }
          }, "驳回"), h("Button", {
            props: {
              type: "error",
              size: "small",
              icon: "md-trash",
              ghost: true
            },
            on: {
              click: () => {
                this.remove(params.row);
              }
            }
          }, "删除")]);
        }
      }],
      data: [],
      // 表单数据
      pageNumber: 1,
      // 当前页数
      pageSize: 10,
      // 页面大小
      total: 0,
      // 表单数据总数
      showFilterPanelFlag: false,
      mySelectRow: {}
    };
  },
  methods: {
    init() {
      this.getDataList();
    },
    uploadFileFx(e) {
      this.mySelectRow = e;
      this.uploadFileMoadl = true;
    },
    changeTaskFile(e) {
      console.log(e);
      var that = this;
      uploadFile({
        id: that.mySelectRow.id,
        file: e.url
      }).then(res => {
        if (res.success) {
          that.uploadFileMoadl = false;
          this.$Message.success("上传成功");
        }
      });
    },
    checkNotTopicFx(e) {
      var that = this;
      checkNotTopic({
        id: e.id
      }).then(res => {
        if (res.success) {
          this.$Message.success("驳回成功");
          that.getDataList();
        }
      });
    },
    auditFx(e) {
      var that = this;
      auditTopic({
        id: e.id
      }).then(res => {
        if (res.success) {
          this.$Message.success("审核成功");
          that.getDataList();
        }
      });
    },
    downFileFx(e) {
      window.open(e.taskFile);
    },
    submited() {
      this.currView = "index";
      this.getDataList();
    },
    changePage(v) {
      this.searchForm.pageNumber = v;
      this.getDataList();
      this.clearSelectAll();
    },
    changePageSize(v) {
      this.searchForm.pageSize = v;
      this.getDataList();
    },
    rowClick(row, index) {
      this.selectRow = row;
    },
    rowClassNmae(row, index) {
      if (row.id == this.selectRow.id) {
        return "rowClassNmaeColor";
      }
      return "";
    },
    excelData() {
      this.$refs.table.exportCsv({
        filename: "导出结果"
      });
    },
    handleSearch() {
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 15;
      this.getDataList();
    },
    handleReset() {
      this.$refs.searchForm.resetFields();
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 15;
      // 重新加载数据
      this.getDataList();
    },
    changeSort(e) {
      this.searchForm.sort = e.key;
      this.searchForm.order = e.order;
      if (e.order === "normal") {
        this.searchForm.order = "";
      }
      this.getDataList();
    },
    clearSelectAll() {
      this.$refs.table.selectAll(false);
    },
    changeSelect(e) {
      this.selectList = e;
      this.selectCount = e.length;
    },
    getDataList() {
      this.loading = true;
      getTopicList(this.searchForm).then(res => {
        this.loading = false;
        if (res.success) {
          this.data = res.result.records;
          this.total = res.result.total;
        }
      });
    },
    add() {
      this.currView = "add";
    },
    edit(v) {
      // 转换null为""
      for (let attr in v) {
        if (v[attr] == null) {
          v[attr] = "";
        }
      }
      let str = JSON.stringify(v);
      let data = JSON.parse(str);
      this.formData = data;
      this.currView = "edit";
    },
    remove(v) {
      this.$Modal.confirm({
        title: "确认删除",
        // 记得确认修改此处
        content: "您确认要删除 " + v.name + " ?",
        loading: true,
        onOk: () => {
          // 删除
          deleteTopic({
            ids: v.id
          }).then(res => {
            this.$Modal.remove();
            if (res.success) {
              this.$Message.success("操作成功");
              this.getDataList();
            }
          });
        }
      });
    },
    delAll() {
      if (this.selectCount <= 0) {
        this.$Message.warning("您还未选择要删除的数据");
        return;
      }
      this.$Modal.confirm({
        title: "确认删除",
        content: "您确认要删除所选的 " + this.selectCount + " 条数据?",
        loading: true,
        onOk: () => {
          let ids = "";
          this.selectList.forEach(function (e) {
            ids += e.id + ",";
          });
          ids = ids.substring(0, ids.length - 1);
          // 批量删除
          deleteTopic({
            ids: ids
          }).then(res => {
            this.$Modal.remove();
            if (res.success) {
              this.$Message.success("操作成功");
              this.clearSelectAll();
              this.getDataList();
            }
          });
        }
      });
    }
  },
  mounted() {
    this.init();
    this.tableHeight = Number(window.innerHeight - 273);
    this.mycolumns = this.columns;
    let showcolumns = [];
    for (var i = 0; i < this.selected.length; i++) {
      var item = this.selected[i];
      for (var j = 0; j < this.columns.length; j++) {
        if (this.columns[j].title == item) {
          showcolumns.push(this.columns[j]);
        }
      }
    }
    this.columns = showcolumns;
  },
  watch: {
    selected: function (newcolumns) {
      let showcolumns = [];
      for (var i = 0; i < this.mycolumns.length; i++) {
        var item = this.mycolumns[i];
        if (item.title == undefined) showcolumns.push(item);else if (newcolumns.contains(item.title)) showcolumns.push(item);
      }
      this.columns = showcolumns;
    }
  }
};
